import{o as e,c as t,b as r}from"./app.81d1f2af.js";const i='{"title":"Usability","description":"","frontmatter":{},"headers":[{"level":2,"title":"5 key points","slug":"_5-key-points"}],"relativePath":"design/usability/usability.md","lastUpdated":1723712345098}',s={},o=r('<h1 id="usability"><a class="header-anchor" href="#usability" aria-hidden="true">#</a> Usability</h1><blockquote><p><em>Usability is a measure of how well a specific user in a specific context can use a product/design to achieve a defined goal effectively, efficiently and satisfactorily.</em></p><p><small>â€” <a href="https://www.interaction-design.org/literature/topics/usability" target="_blank" rel="noopener noreferrer">Interaction Design Foundation</a></small></p></blockquote><p>or just:</p><div class="important"><p><strong>usability means how easy and comfortable a product is to use</strong></p></div><p>Some usability aspects, like clarity, legibility, contrast, hierarchy or proximity, are common across all graphic design areas. But digital user interfaces require some extra concerns, like different screen sizes or light/brightness conditions etc.</p><p>To find out possible weak spots, it is common practice to prototype and test our product before it goes to production. Usability plays important (maybe the most important) part in overall user experience.</p><h2 id="_5-key-points"><a class="header-anchor" href="#_5-key-points" aria-hidden="true">#</a> 5 key points</h2><ol><li><strong>Effectiveness</strong> â€” It supports users in completing actions accurately.</li><li><strong>Efficiency</strong> â€” Users can perform tasks quickly through the easiest process.</li><li><strong>Engagement</strong> â€” Users find it pleasant to use and appropriate for its industry/topic.</li><li><strong>Error Tolerance</strong> â€” It supports a range of user actions and only shows an error in genuine erroneous situations. You achieve this by finding out the number, type and severity of common errors users make, as well as how easily users can recover from those errors.</li><li><strong>Ease of Learning</strong> â€” New users can accomplish goals easily and even more easily on future visits.</li></ol><p>ðŸ“Œ<strong>Read more:</strong> <a href="https://www.interaction-design.org/literature/topics/usability" target="_blank" rel="noopener noreferrer">Interaction-Design.org: Usability</a></p><p>Also:</p><ul><li><a href="https://uxplanet.org/dont-make-me-think-20-wise-thoughts-about-usability-from-steve-krug-876b563f1d63" target="_blank" rel="noopener noreferrer">Don&#39;t make me think!</a></li><li><a href="https://www.userfocus.co.uk/articles/A_CRAP_way_to_improve_usability.html" target="_blank" rel="noopener noreferrer">A CRAP way to improve usability</a></li></ul><p>Other resources:</p><ul><li><a href="https://usabilitygeek.com/" target="_blank" rel="noopener noreferrer">Usabilitygeek</a></li><li><a href="https://uxmag.com/topics/usability" target="_blank" rel="noopener noreferrer">UXmag</a></li></ul>',13);s.render=function(r,i,s,a,n,l){return e(),t("div",null,[o])};export default s;export{i as __pageData};
